#ifndef _MIDEA_VERSION_H_
#define _MIDEA_VERSION_H_
//NOTE:hardware big version and hardware small version should merge to one version
#define MS_HW_PLATFORM          0X05 //Hardware platform
#define MS_HW_CATEGORY			0x02 //Hardware category
#define MS_HW_BIG_VERSION       0X01 //hardware big version
#define MS_HW_SMALL_VERSION     0X04 //hardware small version
#define MS_HW_FUNC_VERSION      0X02 //hardware function version
#define MS_SW_VERSION_Y         0X19 //software compile year
#define MS_SW_VERSION_M         0X05 //software compile month
#define MS_SW_BIG_VERSION       0X00 //software big version
#define MS_SW_SMALL_VERSION     0X01 //software small version
#define MS_SW_FUNC_VERSION      0X16 //software function version

#endif
